window.addEventListener("DOMContentLoaded",function(){let e=document.querySelector("#name-mouse-listener"),t=document.querySelector("#text-name");e.addEventListener("mousemove",function(o){let n=o.clientX-e.offsetLeft,s=o.clientY-e.offsetTop;t.style.setProperty("--mouse-x",n+"px"),t.style.setProperty("--mouse-y",s+"px")});let o=document.querySelectorAll(".instagram-conic-gradient");o.forEach(e=>{e.addEventListener("mousemove",function(t){let o=t.clientX-e.offsetLeft,n=t.clientY-e.offsetTop;e.style.setProperty("--mouse-x",o+"px"),e.style.setProperty("--mouse-y",n+"px")})})});